INSERT INTO `dogs` (`id`, `dog_name`, `size_id`, `origin_id`, `description`, `dog_img`, `view_count`, `created_at`, `updated_at`) VALUES
(1, '柴犬', 1, 1, '飼い主とも適度な距離感を大切にする、独立心が強いツンデレ犬', 'storage/img/shiba01.jpg', 0, '2025-11-16 06:19:02', '2025-11-16 06:19:02'),
(2, '日本テリア', 1, 1, '体調と体高がほぼ同じくらいの短毛の小型犬で、黒や茶色のくさび型の頭に白くすんなりしたボディが個性的な外貌です。明るく活発で警戒心もありますが、いわゆる「テリア気質」は弱く、愛玩犬らしい人懐こさがあります。', 'storage/img/jpteria01.png', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(3, 'ミニチュアプードル', 1, 6, '細い手足に小さな頭、独特の被毛は気品を感じさせます。大変聡明で、運動能力があり、好奇心が強い犬種です。', 'storage/img/pudoru01.png', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(4, 'ミニチュアダックスフンド', 1, 4, '体長は体高の2倍の長さという短足胴長の体型で、垂れ耳で中くらいの長さのマズルを持つ小型犬です。', 'storage/img/dakkusu01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(5, 'シェットランドシープドッグ', 1, 5, '体長が体高よりやや長く、筋肉質で身軽です。首まわりから胸にかけて襟巻のような豪華な飾り毛があります。また、全体にオスの方が毛量が豊かなことが多いようです。', 'storage/img/sheruthi01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(6, 'キャバリア', 1, 5, '身のこなしが優雅でバタバタせず、優しく穏やかな表情をしています。人間が大好きな、やや大きめの小型犬で、体高より体長が長めです。', 'storage/img/kyaba01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(7, 'シーズー', 1, 3, '鼻ぺちゃで垂れ耳、体高より体長がわずかに長く、決して華奢ではないしっかりとした四肢を持っています。', 'storage/img/shizu01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(8, 'ジャックラッセルテリア', 1, 5, '体長が体高よりわずかに長く、白をベースに大きな斑が入る毛色です。体格は筋肉質かつ細身でなくてはいけません。', 'storage/img/jackteria01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(9, 'パグ', 1, 3, 'スクエアな体格とがっちりしていて筋肉質です。短毛で滑らかな毛並み、しわくちゃの顔が特徴です。', 'storage/img/pagu01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(10, 'ペキニーズ', 1, 3, 'がっちりした前足と細めの後ろ足、ずんぐりした首回りですが、全身を覆う長毛のおかげでボリューム感のある、まるで獅子のような貫録のある堂々とした姿をしています。', 'storage/img/peki01.jpg', 0, '2025-11-16 06:35:53', '2025-11-16 06:35:53'),
(11, '甲斐犬', 2, 1, '立ち耳で巻尾または差尾、しっかりとした四肢と体格を持つ中型の日本犬で、虎毛が特徴。現役の猟犬として働く個体もおり、愛玩犬化が好まれず、気性の強さも大切な要件として繁殖されている犬です。', 'storage/img/kai01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(12, '紀州犬', 2, 1, '体高より体長がやや長く、胸は深く背中はまっすぐで、しっかりと力強い四肢をしています。落ち着いた性格で、成犬になるとはしゃぐようなことは少なく、まじめさや忠実さがあります。', 'storage/img/kishu01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(13, '四国犬', 2, 1, '体高より体長がやや長く、この傾向はメスの方が顕著です。背中からしっぽの付け根まではまっすぐで、身体の幅に合った前足と、腰幅に合った後ろ足があります。飼い主に対しては非常に忠実ですが、それ以外の人には心を許しません。', 'storage/img/shikoku01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(14, '日本スピッツ', 2, 1, '体高より体長がやや長く、真っ白な長毛に覆われています。活発で遊び好き、好奇心が強いのに警戒心も強く、無駄吠えがひどいと言われてきました。しかし近年は、穏やかな性格の個体が増えており、環境が整っていれば1日中大騒ぎする程のことはありません。', 'storage/img/jpspittsu01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(15, 'スタンダードダックスフンド', 2, 4, '特徴的な短足胴長の体型で、口先はやや長め、筋肉がよく発達した垂れ耳の犬です。明るく活発で、好奇心が強く、物怖じしない性格です。', 'storage/img/stdakkusu01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(16, 'ウェルシュ・コーギー・ペンブローク', 2, 5, '体高に比べて体長が長く、体は筋肉質でがっしりとしており、活発に動きます。スタミナがあり、使役犬らしく人間とともに動くことを好みます。', 'storage/img/kogi01.jpg', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(17, 'ビーグル', 2, 5, '短毛で垂れ耳、中ぐらいの長さの口先を持つ、骨太でしっかりした体格を持つ中型犬です。用心深さと大胆さの両面を持ち合わせています。', 'storage/img/biguru01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(18, 'フレンチブルドッグ', 2, 6, '大きな頭と耳を持っていて、しっぽは短く、四肢と胴体は筋肉質でがっちりとしています。遊び好きで元気いっぱい。愛情深く、おおらかで社交的な性格です。', 'storage/img/frebull01.jpg', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(19, 'ブルドッグ', 2, 5, '肉づきがよくがっしりしていますが、肥満であってはいけないとされています。見た目に反して友好的で素直な性格。辛抱強くとても穏やかです。', 'storage/img/bulldog01.jpg', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(20, 'アメリカンスタッフォードシャーテリア', 2, 5, '短毛の下から筋肉の形が見えるような、がっちりとした胴体と体つきをしています。激しく戦っている最中でも、飼い主の呼び声ひとつでピタリと止められる服従心の強さが、この犬種の持ち味であり魅力といえるでしょう。', 'storage/img/amestafoteria01.png', 0, '2025-11-16 06:51:50', '2025-11-16 06:51:50'),
(21, '秋田犬', 3, 1, '足が長く腰高でがっしりとした体格、短毛の大型犬です。賢く冷静で、飼い主に忠実な性格。勇敢に飼い主を守ろうとすることも。', 'storage/img/akita01.jpg', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(22, 'アラスカンマラミュート', 3, 2, '外見からシベリアンハスキーと混同されがちですが、ハスキーとは体の大きさが明らかに違い、アラスカンマラミュートのほうが一回りほど大きいです。また、オオカミのような外見に似合わず、性格は穏やかで従順、人に対して愛情深く、献身的です。', 'storage/img/maramyu01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(23, 'オールドイングリッシュシープドッグ', 3, 5, '体長と体高がほぼ同じのスクエアな体型で、肩より腰の方が高い足長のプロポーションの大型犬です。陽気さと穏やかさのバランスが良く、やんちゃな面を見せたかと思えば、飼い主をよく見て行動する賢さもある犬です。', 'storage/img/oldEngsheepdog01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(24, 'カーリーコーテッドレトリバー', 3, 5, '大型犬で体高より体長がやや長く、しっかりとした首はわずかにカーブしています。原産国イギリスの犬種標準でも「知性を感じさせる」と書かれているほど、賢さを見せる犬種です。', 'storage/img/cuCoaRet01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(25, 'グレートデン', 3, 4, '体長と体高がほぼ同じ長さで、短毛の超大型犬です。頭部はやや大きく、明確なストップがあり、咬合はシザーズバイトです。大きな体に大きな優しさを宿した、温和で友好的な性格です。', 'storage/img/greatdane01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(26, 'グレートピレニーズ', 3, 6, '巨体でありがなら意外と敏しょうであり、優雅かつ迫力のある姿をしています。洞察力があり、自分で考える力があるため、主人より先回りして判断をしようとすることがあります。', 'storage/img/greatPyre01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(27, 'ゴールデンレトリバー', 3, 5, '体高より体長がやや長く、バランスの取れた体つきです。賢く穏やかで、洞察力があり、人にも犬にもフレンドリーです。', 'storage/img/goldenRet01.jpg', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(28, 'ラブラドールレトリバー', 3, 5, 'ラブラドールレトリバーは欧米では中型犬とされることも多いですが、日本では大型犬とされています。穏やかで、知性と順応力が高いです。', 'storage/img/labRet01.jpg', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(29, 'サモエド', 3, 7, '体高より体長がやや長く、全身を白い長毛に覆われた、立ち耳で小さめの大型犬です。優しく温和で辛抱強く、他人にも他犬にもフレンドリーです。', 'storage/img/samoyed01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(30, 'ジャーマン・シェパード・ドッグ', 3, 4, '体高より体長がやや長く、腰の下がった体型をした、立ち耳の大型犬です。活発であるのに落ち着いており、洞察力があって、自分で考える力を持っています。しかし服従心が強く、勇敢でありながら辛抱強いです。', 'storage/img/gerShep01.png', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18'),
(31, 'シベリアンハスキー', 3, 2, '立ち耳で、大変厚い下毛を持つダブルコートの大型犬です。そり犬として活躍していた歴史から、高い社会性をもち、人間やほかの犬に対しても非常にフレンドリーな犬種です。', 'storage/img/sibeHusky01.jpg', 0, '2025-11-16 07:08:18', '2025-11-16 07:08:18');